import React, { useState } from 'react';
import { Alert } from "react-bs-notifier";
import NavBar from './navbar';
import { Redirect } from "react-router-dom";
import "./ranking.css";

export default function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(false);
    const [authenticated, setAuthenticated] = useState('clear');


    function proccessLogin() {
        const url = "http://131.181.190.87:3000/user/login"
        localStorage.setItem('token', 'clear');
        return fetch(url, {
            method: "POST",
            headers: { accept: "application/json", "Content-Type": "application/json" },
            body: JSON.stringify({ email: email, password: password })
        })
            .then((res) => res.json())
            .then((res) => {
                if (!res.error) {
                    setError(false);
                    localStorage.setItem('token', res.token);
                    setAuthenticated(localStorage.getItem('token'))
                }
                else {
                    setError(true);
                    setEmail('');
                    setPassword('');
                }
            })
    }

    if (authenticated === 'clear') {
        return (
            <div>
                <div className="headDiv">
                    <div className="tbox">
                        <div className="tMessage">
                            <div className="title">
                                Happiness 
                            </div>
                        </div>
                    </div>
                    <div className="userLoginDvi">
                        <div>
                        <Error error={error} type="Login" />
                            <p className="lLogin">Email</p>
                            <input 
                                name="email"
                                id="email"
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <br />
                            <p className="lLogin" >Password</p>
                            <input
                                name="password"
                                id="password"
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <br />
                            <button
                                id="submit-button"
                                type="button"
                                onClick={() => { proccessLogin() }}
                            >
                                Login
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
    else if (!error & authenticated !== 'clear') {
        return (
            <div>
                <NavBar />
                <Redirect to="/register" />
            </div>
        )
    }
}

export function Error(props) {
    if (props.error) {
        if (props.code === 409) {
            return (
                <div className="error-div">
                    <Alert type="danger" headline="Fatal Error">
                        {props.type} Email alreday in use
                    </Alert>
                </div> 
            )
        } else{
            return (
                <div className="error-div">
                    <Alert type="danger" headline="Fatal Error">
                        {props.type} Ensure the correct details are used
                    </Alert>
                </div>
            )
        }
    }
    else {
        return (<div></div>)
    }
}
