import React, { useState, useEffect } from "react";
import "./index.css";
import { AgGridReact } from "ag-grid-react";
import { Button, Badge } from "reactstrap";
import { useHistory } from "react-router-dom";
import "ag-grid-community/dist/styles/ag-grid.css";
import "ag-grid-community/dist/styles/ag-theme-balham.css";


export default function App() {
  const [rowData, setRowData] = useState([]);
  const history = useHistory();

  useEffect(() => {
    fetch("https://openlibrary.org/subjects/drama.json?published_in=2000")
      .then((res) => res.json())
      .then((data) => data.works)
      .then((works) =>
        works.map((book) => {
          return {
            title: book.title,
            author: book.authors[0].name,
            editionCount: book.edition_count,
            id: book.cover_id
          };
        })
      )
      .then((books) => setRowData(books));
  }, []);

  const columns = [
    { headerName: "Title", field: "title" },
    { headerName: "Author", field: "author" },
    { headerName: "Edition Count", field: "editionCount" },
    { headerName: "Book ID", field: "id" }
  ];

  return (
    <div className="container">
      <h1>Book Catalogue</h1>
      <p>
        <Badge color="success">{rowData.length}</Badge> Books published in 2000
        in the Drama category
      </p>
      <div
        className="ag-theme-balham"
        style={{
          height: "300px",
          width: "800px"
        }}
      >
        <AgGridReact
          columnDefs={columns}
          rowData={rowData}
          pagination={true}
          paginationPageSize={7}
          onRowClicked={(row) => history.push(`/book?title=${row.data.title}`)}
        />
      </div>
      <Button
        color="info"
        size="sm"
        className="mt-3"
        href="https://openlibrary.org/developers/api"
        target="_blank"
      >
        Go to Open Library API
      </Button>
    </div>
  );
}
