export function Registration() {
    const url = "http://131.181.190.87:3000/user/register"
    if (password) {
        return fetch(url, {
            method: "POST",
            headers: { accept: "application/json", "Content-Type": "application/json" },
            body: JSON.stringify({ email: innerEmail, password: password })
        })
            .then((res) => {
                if (!res.ok) {
                    setErrorCode(res.status)
                    setError(true);
                    setInnerEmail('');
                    setPassword('');
                }
                else {
                    setError(false);
                    setVerified(true);
                }
            })
            .catch((e) => {
            })
    }
    else {
        setError(true);
        setErrorCode(400)
        setInnerEmail('');
        setPassword('');
    }
}



export function loginCall() {
    const url = "http://131.181.190.87:3000/user/login"
    localStorage.setItem('token', 'clear');
    return fetch(url, {
        method: "POST",
        headers: { accept: "application/json", "Content-Type": "application/json" },
        body: JSON.stringify({ email: email, password: password })
    })

    .then((res) => res.json())
    .then((res) => {
        if (!res.error) {
            setError(false);
            localStorage.setItem('token', res.token);
            setVerified(localStorage.getItem('token'))
        }
        else {
            setError(true);
            setEmail('');
            setPassword('');
        }
    })
}
